import { Link } from "react-router-dom";
import styled from "styled-components";

const MultipleShowCaseCard = ({ data }) => {
  const Images = data.data.map((item, index) => {
    return (
      <Link to={`${item.imageLink}`} key={index}>
        <img src={item.image} alt="" />
        <span>{item.imageLinkText}</span>
      </Link>
    );
  });
  return (
    <CardContainer className="flex-column">
      <h1>{data.title}</h1>
      <ImageContainer>{Images}</ImageContainer>
      <Link to={`${data.footerLink}`} className="ShowCardFooterLink">
        {data.footerLinkText}
      </Link>
    </CardContainer>
  );
};

export default MultipleShowCaseCard;

const CardContainer = styled.div`
  min-height: 15rem;
  max-width: 21.25rem;
  padding: 1rem;
  box-shadow: 3px 5px 10px 0px rgba(0, 0, 0, 0.1);
  background-color: white;
  z-index: 1;
  justify-content: space-between;
  gap: 1rem;

  h1 {
    font-size: 1.2rem;
    font-weight: 500;
  }

  a {
    color: var(--Header-color);
    font-size: 0.75rem;
    margin: 0;
  }

  .ShowCardFooterLink {
    color: var(--font-color-blue);
    font-size: 0.8rem;
  }
`;

const ImageContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  width: 100%;
  box-sizing: border-box;

  a {
    width: 100%;
    img {
      width: inherit;
    }
  }
`;
