import React from "react";
import Slider from "react-slick";
import styled from "styled-components";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import { Link } from "react-router-dom";

const TopCarousel = (props) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 10000,
  };
  return (
    <Carousel>
      <ArrowBackIosNewIcon
        fontSize="large"
        className="main-prev-icon main-carousel-button"
      />
      <Slider {...settings} className="slick-carousel-container">
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/img22/Gateway/CEPC/Hero/Pendrives/D47074703_IN_PC-BAU-GW-HeroMSO-Storage-devices-Creatives_3000x1200._CB636151077_.jpg"
            alt=""
          />
        </Link>
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/img22/Fashion/Gateway/BAU/Apr-hero/Apay/Deals-3000._CB623368300_.jpg"
            alt=""
          />
        </Link>
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/img22/Beauty/GW/April/UnrecHero/Apay/Skincare-desktop-Hero._CB623895796_.jpg"
            alt=""
          />
        </Link>
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/IMG20/Home/2022/BAU/ATFGW/Best_find_postpe_coupon_3000x1200._CB635819533_.jpg"
            alt=""
          />
        </Link>
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/img19/BAU_Hero_2022/2x_Xbiz_march22_pc._CB627753471_.jpg"
            alt=""
          />
        </Link>
        <Link to={"/"} className="carousel-slide">
          <img
            src="https://images-eu.ssl-images-amazon.com/images/G/31/img19/BAU_Hero_2022/2x_Xbiz_march22_pc._CB627753471_.jpg"
            alt=""
          />
        </Link>
      </Slider>
      <ArrowForwardIosIcon
        fontSize="large"
        className="main-next-icon main-carousel-button"
      />
    </Carousel>
  );
};

export default TopCarousel;

const Carousel = styled.div`
  /* width: 90%; */
  margin: auto;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  position: relative;

  /* Carousel Slides Styling */
  .carousel-slide {
    height: 600px;
    width: 100%;
    img {
      width: inherit;
      height: inherit;
    }
  }

  /* Carousel Custom Button Styling */
  .main-carousel-button {
    position: absolute;
    top: 6.5rem;
    fill: black;
    pointer-events: none;
    z-index: 500;
    background-color: white;
  }

  .main-prev-icon {
    left: 1.2rem;
  }

  .main-next-icon {
    right: 1.2rem;
  }

  /* Slick Carousel Navigation Styling */
  .slick-carousel-contianer {
    position: relative;
  }
  .slick-dots {
    display: none !important;
  }
  .slick-arrow {
    background-color: transparent;
    width: 5rem;
    height: 15rem;
    position: absolute;
    top: 120px;
    &::after,
    ::before {
      content: "";
    }
    &:hover {
      border: 2px solid white;
      border-radius: 2px;
    }
  }
  .slick-prev {
    /* position: absolute; */
    left: 0;
    z-index: 500;
  }
  .slick-next {
    /* position: absolute; */
    right: 0;
    z-index: 500;
  }
`;
