import styled from "styled-components";
import { TopNav, BottomNav } from "./index";

const Header = () => {
  return (
    <HeaderContainer>
      <TopNav />
      <BottomNav />
    </HeaderContainer>
  );
};

export default Header;

const HeaderContainer = styled.header`
  width: 100%;
  color: white;
  padding: 0.4rem 1rem;
  background-color: rgba(3, 1, 19, 0.9);

  .TopNav {
    display: grid;
    grid-template-columns: repeat(3, 1fr);

    /* Top Nav Left Side */
    .TopNav__left {
      gap: 0.5rem;
      align-items: center;

      /* Top Left Logo */
      .TopNav__left__Logo {
        padding: 0.8rem 0.35rem 0;
        img {
          width: 6.8rem;
          height: 2.2rem;
        }
      }
      /* Top Left Address */
      .TopNav__left__address {
        justify-content: center;
        span {
          margin: 0 0 -5px 1.7rem;
          color: rgba(220, 200, 220, 0.9);
          font-size: 0.9rem;
          font-weight: 600;
        }
        > div {
          display: flex;

          p {
            font-weight: 600;
          }
        }
      }
    }

    /* Top Nav Middle  */
    .TopNav__middle {
      align-items: center;

      input {
        height: 3rem;
      }

      .TopNav__middle--search {
        background-color: orange;
      }
    }
  }
`;
