import { Link } from "react-router-dom";
import styled from "styled-components";

const MultipleShowCaseCard = (props) => {
  const Cards = props.data.map((item, index) => {
    return (
      <CardContainer>
        <h1>{item.title}</h1>
        <ImageContainer>

        </ImageContainer>
        <Link to={`${item.footerLink}`}>{item.footerLinkText}</Link>
      </CardContainer>
    );
  });
  return <>{Cards}</>;
};

export default MultipleShowCaseCard;

const CardContainer = styled.div`
  height: 25rem;
  max-width: 21.25rem;
  padding: 1rem;
  box-shadow: 3px 5px 10px 0px rgba(0, 0, 0, 0.1);
  background-color: white;
  z-index: 1;

  h1 {
    font-size: 1.2rem;
    font-weight: 500;
  }

  a {
    color: var(--font-color-blue);
  }
`;

