import React from "react";
import Slider from "react-slick";
import styled from "styled-components";

const TopCarousel = (props) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 10000,
  };
  return (
    <Carousel>
      <Slider {...settings}>
        <div>1</div>
        <div>2</div>
        <div>3</div>
        <div>4</div>
      </Slider>
    </Carousel>
  );
};

export default TopCarousel;

const Carousel = styled.div`
  /* width: 90%; */
  margin: auto;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  position: relative;

  .slick-arrow {
    background-color: red;
    width: 5rem;
    height: 15rem;
    position: relative;
  }
  .slick-prev {
    position: absolute;
    left: 0;
  }
  .slick-next {
    position: absolute;
    right: 0;
  }
`;
