import React from "react";
import styled from "styled-components";

const SingleShowCaseCard = (props) => {
  return (
    <CardContainer>
      <h1>{props.title}</h1>
      {props.button && <button>{props.buttonText}</button>}
    </CardContainer>
  );
};

export default SingleShowCaseCard;

const CardContainer = styled.div``;
