import { Link } from "react-router-dom";
import styled from "styled-components";

const MultipleShowCaseCard = (props) => {
  return (
    <CardContainer>
      <h1>{props.title}</h1>
      <ul>
        <li>
          <img src={props.product.image} alt="" />
          <span>{props.product.name1}</span>
        </li>
      </ul>
      <Link to={`${props.link}`} className="card_footer_link">
        {props.linkText}
      </Link>
    </CardContainer>
  );
};

export default MultipleShowCaseCard;

const CardContainer = styled.div`
  height: 25rem;
  max-width: 21.25rem;
  padding: 1rem;
  box-shadow: 3px 5px 10px 0px rgba(0, 0, 0, 0.1);
  background-color: white;
  z-index: 1;

  h1 {
    font-size: 1.2rem;
    font-weight: 500;
  }

  a {
    color: var(--font-color-blue);
  }
`;
