import React from "react";
import styled from "styled-components";
import { TopCarousel, carouselData, MultipleShowCaseCard } from "../components";

const Home = () => {
  return (
    <Container>
      <TopCarousel data={carouselData} />
      <div className="cardContainer">
        <MultipleShowCaseCard
          title="Up to 60% off | Styles for Men"
          link="/"
          linkText="See all Offers"
        />
        <MultipleShowCaseCard
          title="Up to 60% off | Styles for Women"
          link="/"
          linkText="See all Offers"
        />
        <MultipleShowCaseCard
          title="Up to 60% off on home appliances"
          link="/"
          linkText="See more"
        />
        <MultipleShowCaseCard title="Sign in for your best experience" />
      </div>
    </Container>
  );
};

export default Home;

const Container = styled.section`
  .cardContainer {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    margin-top: -350px;
    padding: 1rem;
    width: 100%;
    justify-content: space-evenly;
    gap: 1rem;
    background: linear-gradient(
      to top,
      rgba(200, 200, 200, 1),
      rgba(0, 0, 0, 0)
    );

    @media (max-width: 1024px) {
      grid-template-columns: repeat(3, 1fr);
    }
  }
`;
