import { Link } from "react-router-dom";
import styled from "styled-components";

const MultipleShowCaseCard = (props) => {
  return (
    <CardContainer>
      <h1>{props.title}</h1>
      <div>hello</div>
      <Link to={props.link} className="card_footer_link">
        {props.footerLinkText}
      </Link>
    </CardContainer>
  );
};

export default MultipleShowCaseCard;

const CardContainer = styled.div`
  height: 25rem;
  max-width: 21.25rem;
  padding: 1rem;
  box-shadow: 3px 5px 10px 0px rgba(0, 0, 0, 0.1);
  background-color: white;

  h1 {
    font-size: 1.2rem;
    font-weight: 500;
  }
`;
