import { Link } from "react-router-dom";
import styled from "styled-components";

const MultipleShowCaseCard = ({ data }) => {
  console.log(data);
  const CardData = data.map((item, index) => {
    const Images = item.data.map((item, index) => {
      return (
        <ImageContainer>
          <img src={item.image} alt="" />
          <Link to={`${item.imageLink}`}>{item.imageLinkText}</Link>
        </ImageContainer>
      );
    });
    return (
      <CardContainer key={index}>
        <CardContainer key={index}>
          <h1>{item.title}</h1>
          {Images}
          <Link to={`${item.footerLink}`}>{item.footerLinkText}</Link>
        </CardContainer>
      </CardContainer>
    );
  });
  return <>{CardData}</>;
};

export default MultipleShowCaseCard;

const CardContainer = styled.div`
  height: 25rem;
  max-width: 21.25rem;
  padding: 1rem;
  box-shadow: 3px 5px 10px 0px rgba(0, 0, 0, 0.1);
  background-color: white;
  z-index: 1;

  h1 {
    font-size: 1.2rem;
    font-weight: 500;
  }

  a {
    color: var(--font-color-blue);
  }
`;

const ImageContainer = styled.div``;
