import React from "react";
import Slider from "react-slick";
import styled from "styled-components";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";

const TopCarousel = (props) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 10000,
  };
  return (
    <Carousel>
      <ArrowBackIosNewIcon className="main-prev-icon main-carousel-button" />
      <Slider {...settings}>
        <div className="main-div">1</div>
        <div className="main-div">2</div>
        <div className="main-div">3</div>
        <div className="main-div">4</div>
      </Slider>
      <ArrowForwardIosIcon className="main-next-icon main-carousel-button" />
    </Carousel>
  );
};

export default TopCarousel;

const Carousel = styled.div`
  /* width: 90%; */
  margin: auto;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  position: relative;

  .main-div {
    width: 100%;
    height: 15rem;
  }

  .slick-arrow {
    background-color: transparent;
    width: 5rem;
    height: 15rem;
    position: relative;
    &::after,
    ::before {
      content: "";
    }
    &:hover {
      border: 2px solid white;
      border-radius: 2px;
    }
  }
  .slick-prev {
    position: absolute;
    left: 0;
    z-index: 100;
  }
  .slick-next {
    position: absolute;
    right: 0;
  }
`;
